import { useEffect, useState } from "react";

function App() {
  const [points, setPoints] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/points')
      .then(response => response.json())
      .then(data => setPoints(data));
  }, []);

  return (
    <div>
      <h1>我的集點卡</h1>
      <ul>
        {points.map((p, index) => (
          <li key={index}>{p ? "✅" : "❌"}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
